import React, { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../context/authContext";
import "../css/navbar.css";
import { FaUsers } from "react-icons/fa";

const API = process.env.REACT_APP_API_URL ?? "http://localhost:3001";

const Navbar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [pedidos, setPedidos] = useState(0);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const irParaUtilizadores = () => {
    navigate("/users");
  };

  const buscarPedidos = async () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const res = await fetch(`${API}/api/friends/requests`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setPedidos(data.length);
    } catch (err) {
      console.error("Erro ao buscar pedidos:", err.message);
    }
  };

  useEffect(() => {
    if (!user) return;

    buscarPedidos();
    const intervalo = setInterval(buscarPedidos, 10000);

    return () => clearInterval(intervalo);
  }, [user]);

  const isLoginPage = location.pathname === "/login";
  const isSignupPage = location.pathname === "/signup";

  return (
    <nav className="navbar">
      <div className="navbar-left">
        {isLoginPage || isSignupPage ? (
          <span className="logo">Zebify</span>
        ) : (
          <Link to="/" className="logo">
            Zebify
          </Link>
        )}

        {!isLoginPage && !isSignupPage && user && (
          <Link to="/profile">Perfil</Link>
        )}
      </div>

      <div className="navbar-right">
        {!isLoginPage && !isSignupPage && user && (
          <>
            <div
              style={{
                position: "relative",
                marginRight: "15px",
                cursor: "pointer",
              }}
              onClick={irParaUtilizadores}
              title="Ver utilizadores"
            >
              <FaUsers size={20} />
              {pedidos > 0 && (
                <span
                  style={{
                    position: "absolute",
                    top: "-6px",
                    right: "-8px",
                    background: "red",
                    color: "white",
                    borderRadius: "50%",
                    padding: "2px 6px",
                    fontSize: "12px",
                    fontWeight: "bold",
                  }}
                >
                  {pedidos}
                </span>
              )}
            </div>

            <span className="welcome">Bem-vindo, {user.name}</span>

            <button
              className="btn-sair"
              style={{
                backgroundColor: "red",
                color: "white",
                border: "none",
              }}
              onClick={handleLogout}
            >
              Sair
            </button>
          </>
        )}

        {isLoginPage && <Link to="/signup">Registo</Link>}
        {isSignupPage && <Link to="/login">Login</Link>}
      </div>
    </nav>
  );
};

export default Navbar;
