import React, { useState } from "react";
import "../css/style.css";

const API = process.env.REACT_APP_API_URL ?? "http://localhost:3001";

const NewPost = ({ onPostSuccess }) => {
  const [tipo, setTipo] = useState("texto");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState("");
  const [visibility, setVisibility] = useState("public");
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setMensagem("Você precisa estar logado para publicar.");
      return;
    }

    const formData = new FormData();
    formData.append("tipo", tipo);
    formData.append("visibility", visibility);
    if (tipo === "texto") {
      formData.append("conteudo", content);
    }
    if (tipo === "imagem" && image) {
      formData.append("imagem", image);
      formData.append("legenda", caption);
    }

    try {
      const res = await fetch(`${API}/api/posts`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      if (!res.ok) {
        const msg = (await res.json()).error || "Erro ao publicar.";
        throw new Error(msg);
      }

      await res.json();
      setMensagem("Post criado com sucesso!");
      setContent("");
      setImage(null);
      setCaption("");
      setVisibility("public");
      if (onPostSuccess) onPostSuccess();
    } catch (err) {
      console.error("Erro ao publicar:", err.message);
      setMensagem(err.message || "Erro ao conectar com o servidor.");
    }
  };

  return (
    <div style={{ marginBottom: "30px" }}>
      <h3>Nova Publicação</h3>

      <div style={{ marginBottom: "10px" }}>
        <button onClick={() => setTipo("texto")} disabled={tipo === "texto"}>
          Texto
        </button>
        <button onClick={() => setTipo("imagem")} disabled={tipo === "imagem"}>
          Imagem
        </button>
      </div>

      <div style={{ margin: "10px 0", display: "flex", gap: "10px" }}>
        <button
          type="button"
          onClick={() => setVisibility("public")}
          style={{
            padding: "8px 16px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            backgroundColor: visibility === "public" ? "#007bff" : "#f0f0f0",
            color: visibility === "public" ? "white" : "black",
            cursor: "pointer",
          }}
        >
          🌍 Público
        </button>

        <button
          type="button"
          onClick={() => setVisibility("friends")}
          style={{
            padding: "8px 16px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            backgroundColor: visibility === "friends" ? "#28a745" : "#f0f0f0",
            color: visibility === "friends" ? "white" : "black",
            cursor: "pointer",
          }}
        >
          🤝 Apenas amigos
        </button>
      </div>

      <div class="container mt-4">

        <div class="position-relative">
          <textarea
            class="form-control pe-5"
            rows="4"
            placeholder="O que está pensando?"
          ></textarea>
          <label
            for="imageUpload"
            class="image-icon position-absolute"
            title="Adicionar imagem"
          >
            📷
          </label>
          <input type="file" id="imageUpload" accept="image/*" class="d-none" />
        </div>
      </div>

      {tipo === "texto" && (
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="O que você está pensando?"
          rows={4}
          style={{ width: "100%", marginBottom: "10px" }}
        />
      )}

      {tipo === "imagem" && (
        <>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
          <input
            type="text"
            placeholder="Legenda"
            value={caption}
            onChange={(e) => setCaption(e.target.value)}
            style={{ width: "100%", marginTop: "10px" }}
          />
        </>
      )}

      <button onClick={handleSubmit} style={{ marginTop: "10px" }}>
        Publicar
      </button>

      {mensagem && <p style={{ color: "green" }}>{mensagem}</p>}
    </div>
  );
};

export default NewPost;
// O código acima é um componente React para criar novas postagens em uma rede social.
// Ele permite que o usuário escolha entre postar texto ou imagem, defina a visibilidade (pública ou apenas amigos) e envie o conteúdo para o servidor.
// O componente utiliza o estado local para gerenciar os dados do formulário e exibe mensagens de sucesso ou erro após a tentativa de publicação.
