import React, { useEffect, useState } from "react";

const API = process.env.REACT_APP_API_URL ?? "http://localhost:3001";
const token = localStorage.getItem("token");

const UsersPage = () => {
  const [utilizadores, setUtilizadores] = useState([]);

  const carregarUtilizadores = async () => {
    try {
      const res = await fetch(`${API}/api/users/with-status`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setUtilizadores(data);
    } catch (err) {
      alert("Erro ao carregar utilizadores");
    }
  };

  useEffect(() => {
    carregarUtilizadores();
  }, []);

  const enviarPedido = async (id) => {
    await fetch(`${API}/api/friends/request`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ receiverId: id }),
    });
    carregarUtilizadores();
  };

  const cancelarPedido = async (id) => {
    await fetch(`${API}/api/friends/request/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    carregarUtilizadores();
  };

  const aceitarPedido = async (id) => {
    await fetch(`${API}/api/friends/accept`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ senderId: id }),
    });
    carregarUtilizadores();
  };

  const recusarPedido = async (id) => {
    await fetch(`${API}/api/friends/reject`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ senderId: id }),
    });
    carregarUtilizadores();
  };

  const removerAmigo = async (id) => {
    await fetch(`${API}/api/friends/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    carregarUtilizadores();
  };

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>Utilizadores</h1>
      {utilizadores.map((u) => (
        <div
          key={u.id}
          className="card"
          style={{
            marginBottom: "10px",
            padding: "10px",
            border: "1px solid #ccc",
          }}
        >
          <p>
            <strong>{u.name}</strong> - {u.email}
          </p>

          {u.status === "amigos" && (
            <button
              onClick={() => removerAmigo(u.id)}
              style={{ backgroundColor: "red", color: "white" }}
            >
              Remover amigo
            </button>
          )}

          {u.status === "pendente" && (
            <button onClick={() => cancelarPedido(u.id)}>
              Cancelar pedido
            </button>
          )}

          {u.status === "recebido" && (
            <>
              <button
                onClick={() => aceitarPedido(u.id)}
                style={{
                  backgroundColor: "green",
                  color: "white",
                  marginRight: "8px",
                }}
              >
                Aceitar
              </button>
              <button
                onClick={() => recusarPedido(u.id)}
                style={{ backgroundColor: "red", color: "white" }}
              >
                Recusar
              </button>
            </>
          )}

          {u.status === "nenhum" && (
            <button onClick={() => enviarPedido(u.id)}>Adicionar amigo</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default UsersPage;
