import { useEffect, useState } from "react";

const API = process.env.REACT_APP_API_URL ?? "http://localhost:3001";

export default function CommentBox({ postId }) {
  const [comentarios, setComentarios] = useState([]);
  const [novoComentario, setNovoComentario] = useState("");
  const [editando, setEditando] = useState(null); // { id, conteudo }
  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user"));

  const carregarComentarios = async () => {
    try {
      const res = await fetch(`${API}/api/comments/${postId}`);
      const data = await res.json();
      setComentarios(data);
    } catch (err) {
      console.error("Erro ao carregar coment치rios:", err.message);
    }
  };

  useEffect(() => {
    carregarComentarios();
  }, [postId]);

  const enviarComentario = async () => {
    if (!novoComentario.trim()) return;
    try {
      const res = await fetch(`${API}/api/comments`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ postId, conteudo: novoComentario }),
      });

      if (!res.ok) throw new Error(await res.text());
      setNovoComentario("");
      carregarComentarios();
    } catch (err) {
      alert("Erro ao comentar: " + err.message);
    }
  };

  const apagarComentario = async (id) => {
    try {
      const res = await fetch(`${API}/api/comments/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!res.ok) throw new Error(await res.text());
      carregarComentarios();
    } catch (err) {
      alert("Erro ao apagar: " + err.message);
    }
  };

  const salvarEdicao = async (id, conteudo) => {
    try {
      const res = await fetch(`${API}/api/comments/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ conteudo }),
      });
      if (!res.ok) throw new Error(await res.text());
      setEditando(null);
      carregarComentarios();
    } catch (err) {
      alert("Erro ao editar: " + err.message);
    }
  };

  return (
    <div style={{ marginTop: "10px" }}>
      <h4>Coment치rios</h4>
      {comentarios.map((c) => (
        <div
          key={c.id}
          style={{
            marginBottom: "8px",
            paddingLeft: "10px",
            borderLeft: "2px solid #ccc",
          }}
        >
          <strong>{c.autor}:</strong>{" "}
          {editando?.id === c.id ? (
            <>
              <input
                type="text"
                value={editando.conteudo}
                onChange={(e) =>
                  setEditando({ ...editando, conteudo: e.target.value })
                }
                style={{ marginRight: "5px" }}
              />
              <button onClick={() => salvarEdicao(c.id, editando.conteudo)}>
                Salvar
              </button>
              <button onClick={() => setEditando(null)}>Cancelar</button>
            </>
          ) : (
            <>
              <span>{c.conteudo}</span>
              {user?.id === c.user_id && (
                <>
                  <button
                    onClick={() =>
                      setEditando({ id: c.id, conteudo: c.conteudo })
                    }
                    style={{ marginLeft: "10px" }}
                  >
                    Editar
                  </button>
                  <button onClick={() => apagarComentario(c.id)}>Apagar</button>
                </>
              )}
            </>
          )}
        </div>
      ))}

      <div style={{ marginTop: "10px" }}>
        <input
          type="text"
          value={novoComentario}
          onChange={(e) => setNovoComentario(e.target.value)}
          placeholder="Escreva um coment치rio..."
          style={{ width: "80%", marginRight: "5px" }}
        />
        <button onClick={enviarComentario}>Comentar</button>
      </div>
    </div>
  );
}
