import React, { useState } from "react";

const API = process.env.REACT_APP_API_URL ?? "http://localhost:3001";

const NewPost = ({ onPostSuccess }) => {
  const [tipo, setTipo] = useState("texto");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState("");
  const [visibility, setVisibility] = useState("public");
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setMensagem("Voc√™ precisa estar logado para publicar.");
      return;
    }

    const formData = new FormData();
    formData.append("tipo", tipo);
    formData.append("visibility", visibility);
    if (tipo === "texto") {
      formData.append("conteudo", content);
    }
    if (tipo === "imagem" && image) {
      formData.append("imagem", image);
      formData.append("legenda", caption);
    }

    try {
      const res = await fetch(`${API}/api/posts`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      if (!res.ok) {
        const msg = (await res.json()).error || "Erro ao publicar.";
        throw new Error(msg);
      }

      await res.json();
      setMensagem("Post criado com sucesso!");
      setContent("");
      setImage(null);
      setCaption("");
      setVisibility("public");
      if (onPostSuccess) onPostSuccess();
    } catch (err) {
      console.error("Erro ao publicar:", err.message);
      setMensagem(err.message || "Erro ao conectar com o servidor.");
    }
  };

  return (
    <div style={{ marginBottom: "30px" }}>
      <h3>Nova Publica√ß√£o</h3>

      <div style={{ margin: "10px 0", display: "flex", gap: "10px" }}>
        <button
          type="button"
          onClick={() => setVisibility("public")}
          style={{
            padding: "8px 16px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            backgroundColor: visibility === "public" ? "#007bff" : "#f0f0f0",
            color: visibility === "public" ? "white" : "black",
            cursor: "pointer",
          }}
        >
          üåç P√∫blico
        </button>

        <button
          type="button"
          onClick={() => setVisibility("friends")}
          style={{
            padding: "8px 16px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            backgroundColor: visibility === "friends" ? "#28a745" : "#f0f0f0",
            color: visibility === "friends" ? "white" : "black",
            cursor: "pointer",
          }}
        >
          ü§ù Apenas amigos
        </button>
      </div>

      {/* Caixa de texto com bot√£o embutido */}
      <div style={{ position: "relative", marginTop: "10px" }}>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="O que est√° pensando?"
          rows={4}
          style={{
            width: "100%",
            padding: "15px 50px 15px 15px",
            fontSize: "16px",
            borderRadius: "12px",
            border: "1px solid #ccc",
            resize: "vertical",
            boxSizing: "border-box",
          }}
        />

        <label
          htmlFor="imageUpload"
          title="Adicionar imagem"
          style={{
            position: "absolute",
            right: "15px",
            bottom: "15px",
            fontSize: "22px",
            cursor: "pointer",
            opacity: 0.6,
            transition: "all 0.2s ease",
          }}
          onMouseOver={(e) => (e.currentTarget.style.opacity = 1)}
          onMouseOut={(e) => (e.currentTarget.style.opacity = 0.6)}
        >
          üì∑
        </label>
        <input
          type="file"
          id="imageUpload"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
          style={{ display: "none" }}
        />
      </div>

      {image && (
        <div style={{ marginTop: "10px" }}>
          <p>Pr√©-visualiza√ß√£o da imagem:</p>
          <img
            src={URL.createObjectURL(image)}
            alt="Preview"
            style={{
              maxWidth: "100%",
              maxHeight: "300px",
              borderRadius: "8px",
              border: "1px solid #ccc",
            }}
          />
        </div>
      )}

      <button onClick={handleSubmit} style={{ marginTop: "10px" }}>
        Publicar
      </button>

      {mensagem && <p style={{ color: "green" }}>{mensagem}</p>}
    </div>
  );
};

export default NewPost;
// O c√≥digo acima √© um componente React para criar novas postagens em uma rede social.
// Ele permite que o usu√°rio escolha entre postar texto ou imagem, defina a visibilidade (p√∫blica ou apenas amigos) e envie o conte√∫do para o servidor.
// O componente utiliza o estado local para gerenciar os dados do formul√°rio e exibe mensagens de sucesso ou erro ap√≥s a tentativa de publica√ß√£o.
