import React, { useState } from "react";

const NewPost = ({ onPostSuccess }) => {
  const [tipo, setTipo] = useState("texto");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState("");
  const [mensagem, setMensagem] = useState("");

  const token = localStorage.getItem("token");

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append("tipo", tipo);
    if (tipo === "texto") formData.append("conteudo", content);
    if (tipo === "imagem") {
      formData.append("imagem", image);
      formData.append("legenda", caption);
    }

    const res = await fetch("http://localhost:3001/posts", {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    const data = await res.json();
    if (res.ok) {
      setMensagem("Post criado!");
      setContent("");
      setImage(null);
      setCaption("");
      if (onPostSuccess) onPostSuccess();
    } else {
      setMensagem(data.error || "Erro ao publicar.");
    }
  };

  return (
    <div>
      <h3>Nova Publicação</h3>
      <div>
        <button onClick={() => setTipo("texto")}>Texto</button>
        <button onClick={() => setTipo("imagem")}>Imagem</button>
      </div>

      {tipo === "texto" && (
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="O que você está pensando?"
        />
      )}

      {tipo === "imagem" && (
        <>
          <input type="file" onChange={(e) => setImage(e.target.files[0])} />
          <input
            type="text"
            placeholder="Legenda"
            value={caption}
            onChange={(e) => setCaption(e.target.value)}
          />
        </>
      )}

      <button onClick={handleSubmit}>Publicar</button>
      {mensagem && <p>{mensagem}</p>}
    </div>
  );
};

export default NewPost;
