// src/pages/UsersPage.js
import React, { useEffect, useState } from "react";
const API = process.env.REACT_APP_API_URL ?? "http://localhost:3001";
const token = localStorage.getItem("token");
const user = JSON.parse(localStorage.getItem("user"));

const UsersPage = () => {
  const [utilizadores, setUtilizadores] = useState([]);

  const carregarUtilizadores = async () => {
    try {
      const res = await fetch(`${API}/api/users/with-status`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setUtilizadores(data);
    } catch (err) {
      alert("Erro ao carregar utilizadores");
    }
  };

  useEffect(() => {
    carregarUtilizadores();
  }, []);

  // Aqui você pode usar as mesmas funções do profile: aceitarPedido, enviarPedido, etc.
  const enviarPedido = async (id) => {
    await fetch(`${API}/api/friends/request`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ receiverId: id }),
    });
    carregarUtilizadores();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Utilizadores</h1>
      {utilizadores.map((u) => (
        <div key={u.id} className="card">
          <p>
            <strong>{u.name}</strong> - {u.email}
          </p>

          {u.status === "amigos" && <button disabled>Amigos</button>}
          {u.status === "pendente" && <button disabled>Pendente</button>}
          {u.status === "recebido" && <button disabled>Pedido recebido</button>}
          {u.status === "nenhum" && (
            <button onClick={() => enviarPedido(u.id)}>Adicionar amigo</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default UsersPage;
